sys_variable_value.value:
(function execute(inputs, outputs) {

  var payload = {};

	var headerObject = new sn_psm_sap_int.PsmSapUtils().executeAction('sn_psm_sap_int.generate_ecc_po_header_object',{po_header:inputs.psm_po}).purchase_order_header;

	if(inputs.psm_po.purchase_order_type == 'standard'){

		payload = new sn_psm_sap_int.PsmSapUtils().generateStandardPOPaylaod(inputs.psm_po.sys_id.toString(), headerObject['ITEM_INTVL'],'ecc');

	}else if(inputs.psm_po.purchase_order_type == 'blanket'){

		payload = new sn_psm_sap_int.PsmSapUtils().generateBlanketPOPayload(inputs.psm_po.sys_id.toString(), headerObject['ITEM_INTVL'],'ecc');

	}

	outputs.poheader = headerObject;
	outputs.polines = payload['polines'];
	outputs.poaccounts = payload['poaccounts'];
	if(payload.hasOwnProperty('poschedules')){
		outputs.poschedules = payload['poschedules'];
	}
	if(payload.hasOwnProperty('poaddress')){
		outputs.poaddress = payload['poaddress'];
	}
	if(payload.hasOwnProperty('polimits')){
		outputs.polimits = payload['polimits'];
	}
	if(payload.hasOwnProperty('poservices')){
		outputs.poservices = payload['poservices'];
	}
	if(payload.hasOwnProperty('poserviceaccess')){
		outputs.poserviceaccess = payload['poserviceaccess'];
	}
 })(inputs, outputs);

