sys_variable_value.value:
(function execute(inputs, outputs) {

  	var gr_record = inputs.gr_record;
    var purchasedReceived = inputs.purchased_received;
   	var percentage = inputs.percentage_quantity;
    var purhcaseLine = inputs.purchase_line;
    var erpNumber = inputs.erp_number;
    var interationCount = parseInt(inputs.iteration_count);
  	var service = inputs.service;
	var short_text = inputs.short_text;

    var serviceGrHeader = new sn_psm_sap_int.PsmSapUtils().getServiceGRHeaderHANA(gr_record,erpNumber);
	var servicesGR = new sn_psm_sap_int.PsmSapUtils().getServiceGRServicesHANA(gr_record, service,interationCount,short_text,purhcaseLine, purchasedReceived,percentage);

  	outputs.service_gr_record = serviceGrHeader;
    outputs.service_gr_services = servicesGR;

})(inputs, outputs);

