sys_variable_value.value:
(function execute(inputs, outputs) {

     var header = JSON.parse(inputs.json);

    // Mandatory field check
    var mandatoryFieldsList = {
      "Legal Entity > ERP Company Code": header.COMP_CODE,
      "Order type": header.DOC_TYPE,
      "Supplier > ERP Suplier Code": header.VENDOR,
      "Purchasing entity > ERP Number": header.PURCH_ORG,
      "Business owner > Department > ID": header.PUR_GROUP,
    };
    var emptyFields = new sn_psm_sap_int.PsmSapUtils().checkMandatoryFields(mandatoryFieldsList);
    if (!gs.nil(emptyFields)) {
      throw new Error('Mandatory field(s) '.concat(emptyFields)+' cannot be blank.');
    }

  outputs.po_header = header;

})(inputs, outputs);

