sys_variable_value.value:
(function execute(inputs, outputs) {
    var fields = new sn_psm_sap_int.PsmSapUtils().getCurrencyFields('sn_shop_purchase_order', inputs.sysid.toString());
    outputs.currency_fields = fields;

    if (inputs.grec.purchase_order_type == "standard") {
        outputs.document_type = "NB";
    } else if (inputs.grec.purchase_order_type == "blanket") {
        outputs.document_type = 'FO';
    }

    if (inputs.grec.status == "pending_cancellation" || inputs.grec.status == "closed_canceled")
        outputs.delete_indicator = 'X';
    else
        outputs.delete_indicator = "";

})(inputs, outputs);

