sys_variable_value.value:
(function execute(inputs, outputs) {
       // ... code ...
           var fields= new sn_psm_sap_int.PsmSapUtils().getCurrencyFields('sn_shop_purchase_order_line',inputs.sysid.toString());
           outputs.currency_fields=fields;
  if(inputs.grec.product_type == 'handling_fee'){
  outputs.price = fields.total_line_amount.amount;
  }else{
    outputs.price = fields.unit_price.amount;
  }
       })(inputs, outputs);

