sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...
        var payload = JSON.parse(inputs.json);
              if(inputs.polRec.product_type == 'handling_fee'){            
              payload['BASE_UOM'] = 'AU';
              }
              outputs.po_services = payload
    })(inputs, outputs);

