sys_variable_value.value:
(function execute(inputs, outputs) {
	// ... code ...
	var payload = JSON.parse(inputs.json);
	if (inputs.grec.product_type == 'service' || inputs.grec.product_type == 'handling_fee') {
		payload['MATERIAL'] = '';
		payload['QUANTITY'] = '';
		payload['PO_UNIT'] = '';
	}

	if (inputs.grec.product_type == 'handling_fee') {
		if (gs.nil(payload['MATL_GROUP'])) {
			payload['MATL_GROUP'] = '007';
		}
	}

	if (inputs.grec.purchase_order.purchase_order_type == 'blanket') {
		payload['MATERIAL'] = '';
	}

	if (payload['ACCTASSCAT'] == 'K' || payload['ACCTASSCAT'] == 'Cost center') {
		payload['ACCTASSCAT'] = 'K';
	} else if (payload['ACCTASSCAT'] == 'A' || payload['ACCTASSCAT'] == 'Asset') {
		payload['ACCTASSCAT'] = 'A';
	} else {
		payload['ACCTASSCAT'] = '';
	}

	outputs.purchase_order_item = payload

})(inputs, outputs);

