sys_variable_value.value:
(function execute(inputs, outputs) {
    var fields = new sn_psm_sap_int.PsmSapUtils().getCurrencyFields('sn_shop_purchase_order_line', inputs.sysid.toString());
    outputs.currency_fields = fields;

    outputs.plantid = new sn_psm_sap_int.PsmSapUtils().getPlantID(inputs.grec);

  if(inputs.grec.product_type == 'handling_fee'){
    outputs.short_description = inputs.grec.short_description;
  }else{
    outputs.short_description = inputs.grec.supplier_product.product_model.short_description;
  }

    var defaultValues = getPOLineDefaultValues(inputs.grec);
    outputs.itemcategory = defaultValues.itemCategory;
    outputs.delete_indicator = defaultValues.deleteIndicator;


    function getPOLineDefaultValues(grec) {
        var returnObj = {};
        if (inputs.grec.purchase_order.purchase_order_type == 'standard') {
            if (inputs.grec.product_type == 'service' || inputs.grec.product_type == 'handling_fee') {
                returnObj['itemCategory'] = 'D';
            }
        } else if (inputs.grec.purchase_order.purchase_order_type == 'blanket') {
            returnObj['itemCategory'] = 'B';
        }

        if (grec.status == "pending_cancellation" || grec.status == "closed_canceled" || grec.purchase_order.status == "closed_canceled" || grec.purchase_order.status == "pending_cancellation")
            returnObj["deleteIndicator"] = 'X';
        else
            returnObj["deleteIndicator"] = "";

        return returnObj;
    }
})(inputs, outputs);

