sys_variable_value.value:
(function execute(inputs, outputs) {
     var input_json = inputs.json_string;
    var delimiter_value = inputs.delimiter.toString().trim();
    var ServiceMasterArray = new sn_sap_data_int.IntegrationsUtils().parseExecuteRFC(input_json, delimiter_value);
    outputs.service_master = ServiceMasterArray.JSONOBJECT;
    outputs.total_rows = ServiceMasterArray.ROWCOUNT;

    for (var i = 0; i < outputs.service_master.length; i++) {
        var recordCreated = JSON.stringify(outputs.service_master[i].ERDAT);
        var lastchange = JSON.stringify(outputs.service_master[i].AEDAT);
        var validityDate = JSON.stringify(outputs.service_master[i].MSTDE);
        outputs.service_master[i].ERDAT = recordCreated.substring(1, 5) + "-" + recordCreated.substring(5, 7) + "-" + recordCreated.substring(7, 9);
        outputs.service_master[i].AEDAT = lastchange.substring(1, 5) + "-" + lastchange.substring(5, 7) + "-" + lastchange.substring(7, 9);
        outputs.service_master[i].MSTDE = validityDate.substring(1, 5) + "-" + validityDate.substring(5, 7) + "-" + validityDate.substring(7, 9);
    }

})(inputs, outputs);

