sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var fields= new sn_psm_sap_int.PsmSapUtils().getCurrencyFields('sn_shop_receipt',inputs.sysid.toString());
  outputs.currency_fields=fields;

  if(inputs.IsReturn){
    outputs.movement_type = '122';
  }else{
    outputs.movement_type = '101';
  }

})(inputs, outputs);

