sys_variable_value.value:
(function execute(inputs, outputs) {

   var line = JSON.parse(inputs.json);
   var mandatoryFieldsList = {
      "Purchase order line > Purchase order > ERP Number" : line.PO_NUMBER,
      "Purchase order line > ERP line number": line.PO_ITEM,
     };
    var emptyFields = new sn_psm_sap_int.PsmSapUtils().checkMandatoryFields(mandatoryFieldsList);
    if (!gs.nil(emptyFields)) {
      throw new Error('Mandatory field(s) '.concat(emptyFields)+' cannot be blank.');
    }

   outputs.gr_line=line;

})(inputs, outputs);

