sys_variable_value.value:
(function execute(inputs, outputs) {
    var table = '';
    var inp = {};
    inp.Material = JSON.parse(inputs.mat_obj);
    var materialMapping = new MaterialMasterMapping();
    var validKeys = ['MARC', 'MARD', 'MBEW', 'ATTYP', 'T006A', 'T023T', 'MAKT', 'T134T'];
    for (var i = 0; i < inp.Material.length; i++) {
        var gr_create = new GlideRecord("sn_psm_sap_int_prodct_stg");
        gr_create.initialize();
        gr_create.setValue('set', inputs.set);
        gr_create.setValue('erp_source', inputs.erp_source);
        var maraArray = inp.Material[i]['MARA'];
        for (var k = 0; k < maraArray.length; k++) {
            var tableKeys = Object.keys(maraArray[k]);
            tableKeys.forEach(function(table_key) {
                if (materialMapping.hasMapping(table_key)) {
                    var mappedField = materialMapping.getMappedField(table_key);
                    gr_create.setValue(mappedField, maraArray[k][table_key]);
                }
            });
            gr_create.insert();
        }

        var material = inp.Material[i];
        for (var key in material) {
            var query = '';
            table = key;

            if (validKeys.indexOf(key) != -1) {
                query = 'material_number=' + inp.Material[i][key][0].MATNR + '^sys_import_set=' + inputs.set;

                if (key == 'MARC') {
                    query += '^plant_fieldISEMPTY';
                } else if (key == 'MARD') {
                    query += '^storage_locationISEMPTY';
                }
            } else {
                continue; // Skip the current iteration and move to the next iteration of the inner loop
            }
            var gr = new GlideRecord("sn_psm_sap_int_prodct_stg");
            gr.addEncodedQuery(query);
            gr.query();
            while (gr.next()) {
                var materialTable = inp.Material[i][table];
                for (var j = 0; j < materialTable.length; j++) {
                    var tableKeys = Object.keys(materialTable[j]);
                    tableKeys.forEach(function(table_key) {
                        if (materialMapping.hasMapping(table_key)) {
                            if (table == 'MAKT' && table_key == 'MAKTX' && materialTable[j]['SPRAS'] == 'E') {
                                gr.setValue(materialMapping.getMappedField(table_key), materialTable[j][table_key]);
                            } else if (table != 'MAKT') {
                                gr.setValue(materialMapping.getMappedField(table_key), materialTable[j][table_key]);
                            }
                        }
                    });
                    gr.update();
                }
            }
        }
    }
})(inputs, outputs);

