sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...

  var grDate = new GlideDateTime(inputs.created);
  var date=grDate.getDate().toString()
  var dates = new sn_psm_sap_int.PsmSapUtils().convertDateToSAPFormat(date);
  outputs.created = dates;

})(inputs, outputs);

