sys_transform_entry.source_script:
answer = (function transformEntry(source) {
    try {
        if (gs.nil(source.material_group) || source.material_group === '') {
            return '';
        }
        var gr_modelCat = new GlideRecord('cmdb_model_category');
        gr_modelCat.addEncodedQuery('code=' + source.material_group + '^ORname=' + source.material_group);
        gr_modelCat.query();
        if (gr_modelCat.next()) {
            if (gs.nil(gr_modelCat.getValue('name'))) {
                gr_modelCat.setValue('name', source.material_group);
            }
            gr_modelCat.setValue('product_type', 'service');
            gr_modelCat.update();
        }
        return source.material_group;
    } catch (ex) {
        gs.error('An error occurred: ' + ex);
        return '';
    }
})(source);
