sys_transform_entry.source_script:
answer = (function transformEntry(source) {

    try {
        if (gs.nil(source.service_category) || source.service_category === '') {
            return '';
        }
        var gr_modelCat = new GlideRecord('cmdb_model_category');
        gr_modelCat.addEncodedQuery('code=' + source.service_category + '^ORname=' + source.service_category);
        gr_modelCat.query();
        if (gr_modelCat.next()) {
            gr_modelCat.setValue('product_type', 'service');
            gr_modelCat.update();
        }
        return source.service_category;
    } catch (ex) {
        gs.error('An error occurred: ' + ex);
        return '';
    }
})(source);
